import React, { useState } from "react";

export function ApplicationForm({ navigate }) {
  const [form, setForm] = useState({
    fullName: "",
    email: "",
    phone: "",
    education: "",
    proficiency: "",
    skills: "",
    experience: "",
    projects: "",
    resumeLink: "",
    portfolioLink: "",
    websiteLink: "",
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // For now we just alert the data. Later you can POST to backend.
    alert("Application Submitted:\n" + JSON.stringify(form, null, 2));
    navigate("/dashboard"); // redirect to dashboard
  };

  return (
    <div className="container">
      <h1>Job Application Form</h1>
      <form onSubmit={handleSubmit}>
        <input
          name="fullName"
          placeholder="Full Name"
          value={form.fullName}
          onChange={handleChange}
          required
        /><br />
        <input
          name="email"
          type="email"
          placeholder="Email"
          value={form.email}
          onChange={handleChange}
          required
        /><br />
        <input
          name="phone"
          placeholder="Phone Number"
          value={form.phone}
          onChange={handleChange}
          required
        /><br />
        <input
          name="education"
          placeholder="Education"
          value={form.education}
          onChange={handleChange}
        /><br />
        <input
          name="proficiency"
          placeholder="Proficiency (e.g., Beginner/Intermediate/Expert)"
          value={form.proficiency}
          onChange={handleChange}
        /><br />
        <textarea
          name="skills"
          placeholder="Skills"
          value={form.skills}
          onChange={handleChange}
        ></textarea><br />
        <input
          name="experience"
          placeholder="Experience (years)"
          value={form.experience}
          onChange={handleChange}
        /><br />
        <textarea
          name="projects"
          placeholder="Projects"
          value={form.projects}
          onChange={handleChange}
        ></textarea><br />
        <input
          name="resumeLink"
          placeholder="Resume Link"
          value={form.resumeLink}
          onChange={handleChange}
        /><br />
        <input
          name="portfolioLink"
          placeholder="Portfolio Link"
          value={form.portfolioLink}
          onChange={handleChange}
        /><br />
        <input
          name="websiteLink"
          placeholder="Website Link"
          value={form.websiteLink}
          onChange={handleChange}
        /><br />
        <button type="submit">Submit Application</button>
      </form>
    </div>
  );
}
