import React from "react";
import { registerUser } from "../api";

export function Register({ navigate }) {
  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = {
      name: e.target.name.value,
      email: e.target.email.value,
      password: e.target.password.value,
      age: e.target.age.value || null,
    };

    try {
      const res = await registerUser(data);
      if (res.success) {
        alert("✅ Registered successfully!");
        navigate("/login");
      } else {
        alert("❌ " + res.error);
      }
    } catch (err) {
      alert("❌ Error: " + err.message);
    }
  };

  return (
    <div className="form-card">
      <h2>Register</h2>
      <form onSubmit={handleSubmit}>
        <input name="name" placeholder="Full Name" type="text" required />
        <input name="email" placeholder="Email" type="email" required />
        <input name="password" placeholder="Password" type="password" required />
        <input name="age" placeholder="Age" type="number" />
        <button type="submit">Register</button>
      </form>
      <p style={{ textAlign: "center" }}>
        Already have an account? <span style={{ color: "blue", cursor: "pointer" }} onClick={() => navigate("/login")}>Login</span>
      </p>
    </div>
  );
}
