import React, { useEffect, useState } from "react";
import { getAllUsers } from "../api";

export function AdminDashboard({ navigate }) {
  const [users, setUsers] = useState([]);

  // Check if admin is logged in
  const admin = JSON.parse(localStorage.getItem("user"));
  useEffect(() => {
    if (!admin || admin.email !== "admin@veridia.com") {
      navigate("/login"); // redirect if not admin
      return;
    }

    const fetchUsers = async () => {
      try {
        const data = await getAllUsers();
        setUsers(data);
      } catch (err) {
        console.log(err);
      }
    };
    fetchUsers();
  }, [admin, navigate]);

  if (!admin || admin.email !== "admin@veridia.com") {
    return <h2>Redirecting to login...</h2>;
  }

  return (
    <div className="container">
      <h1>Admin Dashboard</h1>
      {users.length === 0 ? (
        <p>No registered users yet</p>
      ) : (
        users.map(user => (
          <div key={user._id} style={{ border: "1px solid gray", padding: "10px", margin: "10px 0" }}>
            <h3>{user.name} ({user.email})</h3>
            <h4>Applications:</h4>
            {user.applications && user.applications.length > 0 ? (
              user.applications.map((app, i) => (
                <pre key={i}>{JSON.stringify(app, null, 2)}</pre>
              ))
            ) : (
              <p>No applications submitted</p>
            )}
          </div>
        ))
      )}
    </div>
  );
}
