import React from "react";
import { getAllUsers } from "../api";
import { setUser } from "../handling";

export function Login({ navigate }) {
  const handleSubmit = async (e) => {
    e.preventDefault();
    const email = e.target.email.value;
    const password = e.target.password.value;

    const adminEmail = "admin@veridia.com";
    const adminPassword = "admin123";

    if (email === adminEmail && password === adminPassword) {
      localStorage.setItem("user", JSON.stringify({ email }));
      navigate("/admin");
    } else {
      try {
        const users = await getAllUsers();
        const user = users.find(u => u.email === email && u.password === password);
        if (user) {
          setUser(user);
          navigate("/dashboard");
        } else {
          alert("❌ Invalid email or password");
        }
      } catch (err) {
        alert("Error: " + err.message);
      }
    }
  };

  return (
    <div className="form-card">
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <input name="email" placeholder="Email" type="email" required />
        <input name="password" placeholder="Password" type="password" required />
        <button type="submit">Login</button>
      </form>
      <p style={{ textAlign: "center" }}>
        Don't have an account? <span style={{ color: "blue", cursor: "pointer" }} onClick={() => navigate("/register")}>Register</span>
      </p>
      <p style={{ textAlign: "center" }}>
        Forgot your password? <span style={{ color: "blue", cursor: "pointer" }} onClick={() => navigate("/forgot-password")}>Click here</span>
      </p>
    </div>
  );
}
