import React, { useState } from "react";

export function ApplicationForm({ navigate }) {
  const [form, setForm] = useState({
    fullName: "", email: "", phone: "", education: "", proficiency: "",
    skills: "", experience: "", projects: "", resumeLink: "", portfolioLink: "", websiteLink: ""
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    const user = JSON.parse(localStorage.getItem("user"));
    if (!user) return navigate("/login");

    try {
      const res = await fetch(`http://localhost:5000/users/${user.email}/application`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form)
      });
      const data = await res.json();
      alert(data.message);
      if (data.success) navigate("/dashboard");
    } catch (err) { alert(err.message); }
  };

  return (
    <div className="container">
      <h1>Application Form</h1>
      <form onSubmit={handleSubmit}>
        {Object.keys(form).map(key => (
          <div key={key}>
            <input
              placeholder={key.charAt(0).toUpperCase() + key.slice(1)}
              value={form[key]}
              onChange={e => setForm({ ...form, [key]: e.target.value })}
            /><br />
          </div>
        ))}
        <button type="submit">Submit Application</button>
      </form>
    </div>
  );
}
