import React, { useState } from "react";
import axios from "axios";

export default function ApplicationForm() {
  const [form, setForm] = useState({
    fullName: "",
    email: "",
    phone: "",
    education: "",
    proficiency: "",
    skills: "",
    experience: "",
    projects: "",
    resumeLink: "",
    portfolioLink: "",
    websiteLink: "",
  });

  const [msg, setMsg] = useState("");

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMsg("");

    try {
      const token = localStorage.getItem("token");

      const res = await axios.post(
        "http://localhost:5000/api/applications",
        form,
        {
          headers: {
            Authorization:' Bearer ${token}',
          },
        }
      );

      console.log("✅ Application submitted:", res.data);
      setMsg("🎉 Application submitted successfully!");

      setForm({
        fullName: "",
        email: "",
        phone: "",
        education: "",
        proficiency: "",
        skills: "",
        experience: "",
        projects: "",
        resumeLink: "",
        portfolioLink: "",
        websiteLink: "",
      });
    } catch (err) {
      console.error("❌ Application error:", err);
      setMsg("Failed to submit application. Please try again.");
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100 p-6">
      <div className="bg-white p-6 rounded-2xl shadow-lg w-full max-w-lg">
        <h2 className="text-3xl font-bold mb-4 text-center text-blue-700">
          Veridia Hiring Application Form
        </h2>

        <form onSubmit={handleSubmit}>
          <input
            name="fullName"
            value={form.fullName}
            onChange={handleChange}
            placeholder="Full Name"
            className="w-full p-2 border rounded mb-3"
            required
          />

          <input
            name="email"
            type="email"
            value={form.email}
            onChange={handleChange}
            placeholder="Email"
            className="w-full p-2 border rounded mb-3"
            required
          />

          <input
            name="phone"
            value={form.phone}
            onChange={handleChange}
            placeholder="Phone Number"
            className="w-full p-2 border rounded mb-3"
          />

          <input
            name="education"
            value={form.education}
            onChange={handleChange}
            placeholder="Education (e.g. B.Tech in CSE)"
            className="w-full p-2 border rounded mb-3"
          />

          <input
            name="proficiency"
            value={form.proficiency}
            onChange={handleChange}
            placeholder="Proficiency (Beginner / Intermediate / Expert)"
            className="w-full p-2 border rounded mb-3"
          />

          <input
            name="skills"
            value={form.skills}
            onChange={handleChange}
            placeholder="Skills (e.g. React, Node.js, MongoDB)"
            className="w-full p-2 border rounded mb-3"
          />

          <textarea
            name="experience"
            value={form.experience}
            onChange={handleChange}
            placeholder="Work Experience (e.g. 6 months Intern at Techolution)"
            className="w-full p-2 border rounded mb-3"
            rows="3"
          />

          <textarea
            name="projects"
            value={form.projects}
            onChange={handleChange}
            placeholder="Projects (e.g. Veridia Hiring App, Chatbot using Node.js)"
            className="w-full p-2 border rounded mb-3"
            rows="3"
          />

          <input
            name="resumeLink"
            value={form.resumeLink}
            onChange={handleChange}
            placeholder="Resume Link (Google Drive, PDF, or DOCX)"
            className="w-full p-2 border rounded mb-3"
          />

          <input
            name="portfolioLink"
            value={form.portfolioLink}
            onChange={handleChange}
            placeholder="Portfolio Link (Behance, Dribbble, GitHub, etc.)"
            className="w-full p-2 border rounded mb-3"
          />

          <input
            name="websiteLink"
            value={form.websiteLink}
            onChange={handleChange}
            placeholder="Personal Website Link (optional)"
            className="w-full p-2 border rounded mb-3"
          />

          <button
            type="submit"
            className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700 transition-all"
          >
            Submit Application
          </button>
        </form>

        {msg && (
          <p className="text-center mt-4 text-green-600 font-medium">{msg}</p>
        )}
      </div>
    </div>
  );
}
