import React, { useState } from "react";

export function ApplicationForm({ navigate }) {
  const [form, setForm] = useState({
    fullName: "",
    email: "",
    phone: "",
    education: "",
    collegeName: "",
    proficiency: "", // language dropdown
    skills: [], // selected skills
    experience: "",
    projects: "",
    resumeFile: null, // file upload
    websiteLink: "", // LinkedIn / GitHub / Portfolio
  });

  const skillOptions = [
    "HTML",
    "CSS",
    "JavaScript",
    "React",
    "Node.js",
    "Express",
    "MongoDB",
    "SQL",
    "Python",
    "Java",
    "C++",
    "Git",
    "Docker",
    "AWS",
  ];

  const handleChange = (e) => {
    const { name, value, type, files, selectedOptions } = e.target;

    if (type === "file") {
      setForm({ ...form, [name]: files[0] });
    } else if (name === "skills") {
      const selectedSkills = Array.from(selectedOptions, option => option.value);
      setForm({ ...form, skills: selectedSkills });
    } else {
      setForm({ ...form, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const user = JSON.parse(localStorage.getItem("user"));
    if (!user) {
      alert("❌ Please login first");
      navigate("/login");
      return;
    }

    try {
      const formData = new FormData();
      Object.keys(form).forEach(key => {
        if (key === "skills") {
          formData.append(key, JSON.stringify(form[key]));
        } else {
          formData.append(key, form[key]);
        }
      });

      const res = await fetch(`http://localhost:5000/users/${user._id}/application`, {
        method: "POST",
        body: formData,
      });

      const result = await res.json();
      if (result.success) {
        alert("✅ Application submitted successfully");
        navigate("/dashboard");
      } else {
        alert("❌ Error: " + result.message);
      }
    } catch (err) {
      alert("❌ Error: " + err.message);
    }
  };

  return (
    <div style={{ maxWidth: "600px", margin: "auto" }}>
      <h1>Application Form</h1>
      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <input name="fullName" placeholder="Full Name" value={form.fullName} onChange={handleChange} required /><br />
        <input name="email" placeholder="Email" type="email" value={form.email} onChange={handleChange} required /><br />
        <input name="phone" placeholder="Phone" value={form.phone} onChange={handleChange} required /><br />

        <input name="education" placeholder="Education (Degree e.g., B.Tech, MCA)" value={form.education} onChange={handleChange} required /><br />
        <input name="collegeName" placeholder="College / University Name" value={form.collegeName} onChange={handleChange} required /><br />

        <label>Proficiency (Language you are proficient in)</label><br />
        <select name="proficiency" value={form.proficiency} onChange={handleChange} required>
          <option value="">Select language</option>
          <option value="Telugu">Telugu</option>
          <option value="English">English</option>
          <option value="Hindi">Hindi</option>
          <option value="Tamil">Tamil</option>
          <option value="Kannada">Kannada</option>
          <option value="Other">Other</option>
        </select><br />

        <label>Skills (Select all that apply)</label><br />
        <select name="skills" multiple={true} value={form.skills} onChange={handleChange} required>
          {skillOptions.map(skill => (
            <option key={skill} value={skill}>{skill}</option>
          ))}
        </select><br />

        <input name="experience" placeholder="Experience (in years)" value={form.experience} onChange={handleChange} /><br />
        <input name="projects" placeholder="Projects (comma separated)" value={form.projects} onChange={handleChange} /><br />

        <label>Upload Resume (PDF / JPG / DOCX)</label><br />
        <input name="resumeFile" type="file" onChange={handleChange} accept=".pdf,.doc,.docx,.jpg,.jpeg" required /><br />

        <input name="websiteLink" placeholder="Website link (LinkedIn / GitHub / Portfolio)" value={form.websiteLink} onChange={handleChange} /><br /><br />

        <button type="submit">Submit Application</button>
      </form>
    </div>
  );
}
