import React, { useState } from "react";

export function ApplicationForm({ navigate }) {
  const [form, setForm] = useState({
    fullName: "",
    email: "",
    phone: "",
    education: "",
    proficiency: "",
    skills: "",
    experience: "",
    projects: "",
    resumeLink: "",
    portfolioLink: "",
    websiteLink: ""
  });

  const user = JSON.parse(localStorage.getItem("user"));

  if (!user || user.email === "admin@veridia.com") {
    navigate("/login"); // only normal users
    return null;
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // send application to backend
      const res = await fetch(`http://localhost:5000/users/${user._id}/application`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form)
      });
      const result = await res.json();
      if (result.success) {
        alert("✅ Application submitted!");
        navigate("/dashboard");
      } else {
        alert("❌ Failed to submit application");
      }
    } catch (err) {
      alert("Error: " + err.message);
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h1>Application Form</h1>
      <form onSubmit={handleSubmit}>
        <input
          placeholder="Full Name"
          value={form.fullName}
          onChange={e => setForm({ ...form, fullName: e.target.value })}
          required
        /><br /><br />
        <input
          placeholder="Email"
          type="email"
          value={form.email}
          onChange={e => setForm({ ...form, email: e.target.value })}
          required
        /><br /><br />
        <input
          placeholder="Phone"
          value={form.phone}
          onChange={e => setForm({ ...form, phone: e.target.value })}
        /><br /><br />
        <input
          placeholder="Education"
          value={form.education}
          onChange={e => setForm({ ...form, education: e.target.value })}
        /><br /><br />
        <input
          placeholder="Proficiency"
          value={form.proficiency}
          onChange={e => setForm({ ...form, proficiency: e.target.value })}
        /><br /><br />
        <input
          placeholder="Skills"
          value={form.skills}
          onChange={e => setForm({ ...form, skills: e.target.value })}
        /><br /><br />
        <input
          placeholder="Experience"
          value={form.experience}
          onChange={e => setForm({ ...form, experience: e.target.value })}
        /><br /><br />
        <input
          placeholder="Projects"
          value={form.projects}
          onChange={e => setForm({ ...form, projects: e.target.value })}
        /><br /><br />
        <input
          placeholder="Resume Link"
          value={form.resumeLink}
          onChange={e => setForm({ ...form, resumeLink: e.target.value })}
        /><br /><br />
        <input
          placeholder="Portfolio Link"
          value={form.portfolioLink}
          onChange={e => setForm({ ...form, portfolioLink: e.target.value })}
        /><br /><br />
        <input
          placeholder="Website Link"
          value={form.websiteLink}
          onChange={e => setForm({ ...form, websiteLink: e.target.value })}
        /><br /><br />
        <button type="submit">Submit Application</button>
      </form>
    </div>
  );
}
