import React, { useEffect, useState } from "react";
import axios from "axios";
import Header from "./Header";

export default function Dashboard() {
  const [apps, setApps] = useState([]);
  const [msg, setMsg] = useState("");
  const user = JSON.parse(localStorage.getItem("user") || "{}");
  const token = localStorage.getItem("token");

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    window.location.href = "/login";
  };

  useEffect(() => {
    if (!token) return (window.location.href = "/login");

    axios
      .get("http://localhost:5000/api/applications/mine", {
        headers: { Authorization: 'Bearer ${token}' },
      })
      .then((res) => setApps(res.data))
      .catch(() => setMsg("Error fetching applications"));
  }, [token]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-100 to-blue-50">
      <Header onLogout={handleLogout} />
      <div className="p-6">
        <h2 className="text-2xl font-bold mb-6 text-blue-800">
          Welcome, {user.name ? user.name : "User"} ðŸ‘‹
        </h2>

        <h3 className="text-xl font-semibold text-gray-800 mb-4">
          Your Submitted Applications
        </h3>

        {msg && <p className="text-red-600 mb-4">{msg}</p>}

        {apps.length === 0 ? (
          <p className="text-gray-700 text-center">
            You havenâ€™t submitted any applications yet.
          </p>
        ) : (
          <table className="min-w-full border border-gray-300 rounded-lg shadow-md bg-white">
            <thead className="bg-blue-700 text-white">
              <tr>
                <th className="p-2">Full Name</th>
                <th className="p-2">Education</th>
                <th className="p-2">Proficiency</th>
                <th className="p-2">Status</th>
              </tr>
            </thead>
            <tbody>
              {apps.map((a) => (
                <tr
                  key={a._id}
                  className="text-center border-b hover:bg-blue-50 transition"
                >
                  <td className="p-2">{a.fullName}</td>
                  <td className="p-2">{a.education}</td>
                  <td className="p-2">{a.proficiency}</td>
                  <td
                    className={`p-2 font-semibold ${
                      a.status === "Accepted"
                        ? "text-green-600"
                        : a.status === "Rejected"
                        ? "text-red-600"
                        : "text-gray-700"
                    }`}
                  >
                    {a.status}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}
