import React, { useState } from "react";

export function ApplicationForm({ navigate }) {
  const [form, setForm] = useState({
    fullName: "",
    email: "",
    phone: "",
    education: "",
    collegeName: "",
    proficiency: [], // programming languages / technologies
    skills: "",
    experience: "",
    projects: "",
    resumeLink: "",
    websiteLink: "", // LinkedIn / GitHub / HackerRank / portfolio
  });

  const handleChange = (e) => {
    const { name, value, type, selectedOptions } = e.target;

    if (type === "select-multiple") {
      const values = Array.from(selectedOptions, option => option.value);
      setForm({ ...form, [name]: values });
    } else {
      setForm({ ...form, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const user = JSON.parse(localStorage.getItem("user"));
    if (!user) {
      alert("❌ Please login first");
      navigate("/login");
      return;
    }

    try {
      const res = await fetch(`http://localhost:5000/users/${user._id}/application`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form)
      });
      const result = await res.json();
      if (result.success) {
        alert("✅ Application submitted successfully");
        navigate("/dashboard");
      } else {
        alert("❌ Error: " + result.message);
      }
    } catch (err) {
      alert("❌ Error: " + err.message);
    }
  };

  return (
    <div style={{ maxWidth: "600px", margin: "auto" }}>
      <h1>Application Form</h1>
      <form onSubmit={handleSubmit}>
        <input name="fullName" placeholder="Full Name" value={form.fullName} onChange={handleChange} required /><br />
        <input name="email" placeholder="Email" type="email" value={form.email} onChange={handleChange} required /><br />
        <input name="phone" placeholder="Phone" value={form.phone} onChange={handleChange} required /><br />

        <input name="education" placeholder="Education (Degree e.g., B.Tech, MCA)" value={form.education} onChange={handleChange} required /><br />
        <input name="collegeName" placeholder="College / University Name" value={form.collegeName} onChange={handleChange} required /><br />

        <label>Proficiency (Select all that apply)</label><br />
        <select name="proficiency" multiple value={form.proficiency} onChange={handleChange} required>
          <option value="JavaScript">JavaScript</option>
          <option value="Python">Python</option>
          <option value="Java">Java</option>
          <option value="C++">C++</option>
          <option value="React">React</option>
          <option value="Node.js">Node.js</option>
          <option value="MongoDB">MongoDB</option>
        </select><br />

        <input name="skills" placeholder="Other Skills (comma separated)" value={form.skills} onChange={handleChange} /><br />
        <input name="experience" placeholder="Experience (in years)" value={form.experience} onChange={handleChange} /><br />
        <input name="projects" placeholder="Projects (comma separated)" value={form.projects} onChange={handleChange} /><br />

        <input name="resumeLink" placeholder="Resume link (Google Drive / Dropbox)" value={form.resumeLink} onChange={handleChange} /><br />
        <input name="websiteLink" placeholder="Website link (LinkedIn / GitHub / HackerRank / Portfolio)" value={form.websiteLink} onChange={handleChange} /><br /><br />

        <button type="submit">Submit Application</button>
      </form>
    </div>
  );
}
