import React, {useState} from 'react';
import api from '../api';
export default function Login({onSwitch}){ const [form,setForm]=useState({email:'',password:''});
 const [msg,setMsg]=useState(''); async function submit(e){ e.preventDefault();
   try{ const res = await api.post('/api/auth/login', form); 
    localStorage.setItem('token', res.data.token); 
    localStorage.setItem('user', JSON.stringify(res.data.user)); 
    setMsg('Login successful — check console for redirect handling'); console.log('User:', res.data.user); }catch(err){ setMsg(err.response?.data?.msg || 'Error'); } } return (
  <div className="mt-6 bg-white p-6 rounded shadow">
    <h2 className="text-xl font-semibold">Login</h2>
    <form onSubmit={submit} className="mt-4">
      <input className="w-full p-2 border rounded" placeholder="Email" value={form.email} onChange={e=>setForm({...form,email:e.target.value})} />
      <input className="w-full p-2 border rounded mt-2" placeholder="Password" type="password" value={form.password} onChange={e=>setForm({...form,password:e.target.value})} />
      <div className="mt-4 flex gap-2">
        <button className="px-3 py-2 bg-indigo-600 text-white rounded">Login</button>
        <button type="button" onClick={onSwitch} className="px-3 py-2 border rounded">Create account</button>
      </div>
      <div className="mt-2 text-red-600">{msg}</div>
    </form>
  </div>
); }
