import { RegisterUser } from "./api.js";

export function renderRegister(root, navigate) {
  root.innerHTML = `
    <h1>Register</h1>
    <form id="registerForm">
      <input type="text" id="name" placeholder="Name" required><br>
      <input type="email" id="email" placeholder="Email" required><br>
      <input type="password" id="password" placeholder="Password" required><br>
      <input type="number" id="age" placeholder="Age"><br>
      <button type="submit">Register</button>
    </form>
    <p>Already have an account? <a href="#" id="toLogin">Login</a></p>
    <pre id="output"></pre>
  `;

  const form = document.getElementById("egisterForm");
  const output = document.getElementById("output");

  form.addEventListener("submit", async (e) => {
    e.preventDefault();
    const data = {
      name: document.getElementById("name").value,
      email: document.getElementById("email").value,
      password: document.getElementById("password").value,
      age: parseInt(document.getElementById("age").value) || null
    };

    try {
      const result = await registerUser(data);
      output.textContent = JSON.stringify(result, null, 2);
      if (result.success) navigate("/login");
    } catch (err) {
      output.textContent = "Error: " + err.message;
    }
  });

  document.getElementById("toLogin").addEventListener("click", () => navigate("/login"));
}
