import React, { useState } from "react";
import axios from "axios";

function Register({ onSwitch, onSuccess }) {
  const [form, setForm] = useState({ name: "", email: "", password: "" });
  const [msg, setMsg] = useState("");

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMsg("");
    try {
      const res = await axios.post(
        "http://localhost:5000/api/auth/register",
        form
      );
      console.log(res.data);
      setMsg("✅ Account created successfully! Please log in.");
      setTimeout(() => {
        if (onSuccess) onSuccess(); // ✅ Fix here
      }, 1500);
    } catch (err) {
      setMsg("❌ Registration failed! Try again.");
    }
  };

  return (
    <div className="bg-white p-6 rounded shadow-md w-96">
      <h2 className="text-2xl font-bold mb-4 text-center text-blue-600">
        Create Account
      </h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          placeholder="Full Name"
          value={form.name}
          onChange={handleChange}
          className="w-full p-2 mb-3 border rounded"
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={form.email}
          onChange={handleChange}
          className="w-full p-2 mb-3 border rounded"
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={form.password}
          onChange={handleChange}
          className="w-full p-2 mb-3 border rounded"
        />
        <button
          type="submit"
          className="w-full bg-green-600 text-white py-2 rounded hover:bg-green-700"
        >
          Register
        </button>
      </form>
      <p className="text-sm text-center mt-3">
        Already have an account?{" "}
        <button onClick={onSwitch} className="text-blue-600 font-semibold">
          Login
        </button>
      </p>
      {msg && <p className="text-center mt-3">{msg}</p>}
    </div>
  );
}

export default Register;
